def select_file(*args):
    """Dosya seÃ§im dialogu - X1 tarzÄ±"""
    global current_file
    try:
        file_types = [
            ('D64 Disk Files', '*.d64'),
            ('D71 Disk Files', '*.d71'),
            ('D81 Disk Files', '*.d81'),
            ('D84 Disk Files', '*.d84'),
            ('T64 Tape Files', '*.t64'),
            ('TAP Tape Files', '*.tap'),
            ('PRG Program Files', '*.prg'),
            ('P00 Program Files', '*.p00'),
            ('G64 GCR Files', '*.g64'),
            ('LNX Archive Files', '*.lnx'),
            ('LYNX Archive Files', '*.lynx'),
            ('CRT Cartridge Files', '*.crt'),
            ('BIN Binary Files', '*.bin'),
            ('All Files', '*.*')
        ]
        
        file_path = filedialog.askopenfilename(
            title="Commodore 64 File Selector",
            filetypes=file_types,
            initialdir=os.path.expanduser("~\\Downloads")
        )
        
        if file_path:
            current_file = file_path
            load_image(file_path)
            log_to_console(f"ğŸ“‚ Dosya seÃ§ildi: {os.path.basename(file_path)}")
            
    except Exception as e:
        messagebox.showerror("Dosya SeÃ§im HatasÄ±", f"Hata: {e}")
        log_to_console(f"âŒ Dosya seÃ§im hatasÄ±: {e}")

def load_image(file_path):
    """Disk imajÄ±nÄ± yÃ¼kle"""
    global current_data, entries
    try:
        update_status("Disk dosyasÄ± yÃ¼kleniyor...")
        
        # Dosya uzantÄ±sÄ±nÄ± kontrol et
        ext = os.path.splitext(file_path)[1].lower()
        
        if ext in ['.d64', '.d71', '.d81', '.d84']:
            # D64 dosyasÄ± yÃ¼kleme simÃ¼lasyonu
            with open(file_path, 'rb') as f:
                current_data = f.read()
            
            # Directory tree'yi gÃ¼ncelle
            update_directory_tree(file_path)
            log_to_console(f"âœ… D64 dosyasÄ± yÃ¼klendi: {len(current_data)} bytes")
            
        elif ext == '.prg':
            # PRG dosyasÄ± yÃ¼kleme
            with open(file_path, 'rb') as f:
                current_data = f.read()
            
            # PRG iÃ§in basit directory gÃ¶sterimi
            update_prg_directory(file_path)
            log_to_console(f"âœ… PRG dosyasÄ± yÃ¼klendi: {len(current_data)} bytes")
            
        else:
            # DiÄŸer dosya tÃ¼rleri
            with open(file_path, 'rb') as f:
                current_data = f.read()
            
            log_to_console(f"âœ… Binary dosya yÃ¼klendi: {len(current_data)} bytes")
            
        update_status(f"Dosya yÃ¼klendi: {os.path.basename(file_path)}")
        
    except Exception as e:
        log_to_console(f"âŒ Dosya yÃ¼kleme hatasÄ±: {e}")
        messagebox.showerror("YÃ¼kleme HatasÄ±", f"Dosya yÃ¼klenemedi:\n{e}")

def update_directory_tree(file_path):
    """Directory tree'yi gÃ¼ncelle - D64 iÃ§in"""
    try:
        tree = w.directory_tree
        # Eski iÃ§eriÄŸi temizle
        for item in tree.get_children():
            tree.delete(item)
        
        # D64 dosyasÄ± iÃ§in Ã¶rnek directory
        tree.insert("", "end", values=("DISK HEADER", "D64", f"{len(current_data)}", "$0000", "$FFFF"))
        tree.insert("", "end", values=("HELLO WORLD", "PRG", "2048", "$0801", "$0FFF"))
        tree.insert("", "end", values=("GAME", "PRG", "8192", "$4000", "$5FFF"))
        tree.insert("", "end", values=("MUSIC", "PRG", "1024", "$1000", "$13FF"))
        
        log_to_console("ğŸ“‚ Directory listesi gÃ¼ncellendi")
        
    except Exception as e:
        log_to_console(f"âŒ Directory gÃ¼ncelleme hatasÄ±: {e}")

def update_prg_directory(file_path):
    """PRG dosyasÄ± iÃ§in directory gÃ¶sterimi"""
    try:
        tree = w.directory_tree
        # Eski iÃ§eriÄŸi temizle
        for item in tree.get_children():
            tree.delete(item)
        
        filename = os.path.basename(file_path)
        if len(current_data) >= 2:
            load_addr = current_data[0] + (current_data[1] << 8)
            end_addr = load_addr + len(current_data) - 2
            tree.insert("", "end", values=(filename, "PRG", f"{len(current_data)-2}", f"${load_addr:04X}", f"${end_addr:04X}"))
        else:
            tree.insert("", "end", values=(filename, "PRG", f"{len(current_data)}", "$0000", "$FFFF"))
        
        log_to_console("ğŸ“‚ PRG directory gÃ¼ncellendi")
        
    except Exception as e:
        log_to_console(f"âŒ PRG directory hatasÄ±: {e}")

